allow:
Bash(1 <<'EOF'
const demoGame = await ethers.getContractAt\("DemoGame", "0x8b8792206fcBCbD9C7E175d793eed339c90Dbeb7"\);

console.log\("Registering demo achievements..."\);
const tx = await demoGame.registerDemoAchievements\({ gasLimit: 2000000 }\);
console.log\("TX hash:", tx.hash\);
const receipt = await tx.wait\(\);
console.log\("Confirmed in block:", receipt.blockNumber\);

const registry = await ethers.getContractAt\("AchievementRegistry", "0xaC7EF608641Ae0D8b027C735BbC80C513766219b"\);
const count = await registry.getAchievementCount\(\);
console.log\("\\nAchievement count after registration:", count.toString\(\)\);
for \(let i = 1; i <= count; i++\) {
  const a = await registry.getAchievement\(i\);
  console.log\(`  #${i}: ${a.name} \(${["Common","Rare","Legendary"][a.rarity]}\) - ${a.description}`\);
}
EOF)
